import { Schema, model } from 'mongoose';

const userSchema = new Schema(
  {
    firstName: {type: String,},
    lastName: {type: String,},
    emailId: {type: String,unique:true},
    password:{type: String}
  },
  {
    timestamps: true
  });
export default model('User', userSchema);

