        html: '<h1>To reset your password<a href="http://localhost:3000/api/v1/users/resetPwd/${token}">click here</a></h1>',
      };
  
      const result = await transport.sendMail(mailOptions);
      return result;
    } catch (error) {
      return error;
    }
  }

